SELECT s.student_id,s.student_name,Subjects.subject_name,count(e.subject_name) as attended_exams FROM Students as s JOIN Subjects LEFT JOIN Examinations as e ON s.student_id=e.student_id AND Subjects.subject_name=e.subject_name GROUP BY s.student_name,Subjects.subject_name  ORDER BY s.student_id,Subjects.subject_name;